import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { map, catchError } from 'rxjs/operators';

export interface WeatherResponse {
  location: string;
  temperature: number;
  condition: string;
  description: string;
  humidity: number;
  windSpeed: number;
  icon: string;
  feelsLike: number;
  pressure: number;
  visibility: number;
  sunrise: number;
  sunset: number;
  forecast?: DailyForecast[];
}

export interface DailyForecast {
  date: string;
  tempMax: number;
  tempMin: number;
  condition: string;
  icon: string;
  humidity: number;
}

@Injectable({
  providedIn: 'root'
})
export class WeatherService {
  // OpenWeatherMap API configuration
  private readonly API_KEY = '9a755e38ff059806bf283e3174fdeae7';
  private readonly BASE_URL = 'https://api.openweathermap.org/data/2.5';

  // CORS proxy - you can use cors-anywhere or similar
  // Option 1: Use a public CORS proxy (not recommended for production)
  private readonly CORS_PROXY = 'https://api.allorigins.win/raw?url=';

  // Option 2: For development, you can also use: 'https://corsproxy.io/?'
  // private readonly CORS_PROXY = 'https://corsproxy.io/?';

  constructor(private http: HttpClient) {}

  /**
   * Get weather data by city name
   */
  getWeatherByCity(city: string, units: 'metric' | 'imperial' = 'imperial'): Observable<WeatherResponse> {
    const url = `${this.BASE_URL}/weather?q=${encodeURIComponent(city)}&appid=${this.API_KEY}&units=${units}`;
    const proxiedUrl = `${this.CORS_PROXY}${encodeURIComponent(url)}`;

    return this.http.get<any>(proxiedUrl).pipe(
      map(data => this.transformWeatherData(data, units)),
      catchError(this.handleError)
    );
  }

  /**
   * Get weather data by coordinates
   */
  getWeatherByCoordinates(lat: number, lon: number, units: 'metric' | 'imperial' = 'imperial'): Observable<WeatherResponse> {
    const url = `${this.BASE_URL}/weather?lat=${lat}&lon=${lon}&appid=${this.API_KEY}&units=${units}`;
    const proxiedUrl = `${this.CORS_PROXY}${encodeURIComponent(url)}`;

    return this.http.get<any>(proxiedUrl).pipe(
      map(data => this.transformWeatherData(data, units)),
      catchError(this.handleError)
    );
  }

  /**
   * Get 5-day weather forecast
   */
  getForecast(city: string, units: 'metric' | 'imperial' = 'imperial'): Observable<DailyForecast[]> {
    const url = `${this.BASE_URL}/forecast?q=${encodeURIComponent(city)}&appid=${this.API_KEY}&units=${units}`;
    const proxiedUrl = `${this.CORS_PROXY}${encodeURIComponent(url)}`;

    return this.http.get<any>(proxiedUrl).pipe(
      map(data => this.transformForecastData(data)),
      catchError(this.handleError)
    );
  }

  /**
   * Get current position using browser geolocation
   */
  getCurrentPosition(): Promise<GeolocationCoordinates> {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation is not supported by your browser'));
      } else {
        navigator.geolocation.getCurrentPosition(
          position => resolve(position.coords),
          error => reject(error)
        );
      }
    });
  }

  /**
   * Transform OpenWeatherMap API response to our WeatherResponse format
   */
  private transformWeatherData(data: any, units: string): WeatherResponse {
    // Validate required data exists
    if (!data || !data.main || !data.weather || !data.weather[0] || !data.wind || !data.sys) {
      throw new Error('Invalid API response structure');
    }

    return {
      location: `${data.name || 'Unknown'}${data.sys?.country ? ', ' + data.sys.country : ''}`,
      temperature: Math.round(data.main.temp),
      condition: data.weather[0].main,
      description: data.weather[0].description,
      humidity: data.main.humidity,
      windSpeed: Math.round(data.wind.speed),
      icon: this.getWeatherIcon(data.weather[0].id, data.weather[0].icon),
      feelsLike: Math.round(data.main.feels_like),
      pressure: data.main.pressure,
      visibility: data.visibility || 0,
      sunrise: data.sys.sunrise || 0,
      sunset: data.sys.sunset || 0
    };
  }

  /**
   * Transform forecast data
   */
  private transformForecastData(data: any): DailyForecast[] {
    const dailyData: { [key: string]: any[] } = {};

    // Group forecasts by day
    data.list.forEach((item: any) => {
      const date = new Date(item.dt * 1000).toLocaleDateString();
      if (!dailyData[date]) {
        dailyData[date] = [];
      }
      dailyData[date].push(item);
    });

    // Get daily summary (first 5 days)
    return Object.entries(dailyData).slice(0, 5).map(([date, items]) => {
      const temps = items.map(item => item.main.temp);
      const mainItem = items[Math.floor(items.length / 2)]; // Use midday data

      return {
        date,
        tempMax: Math.round(Math.max(...temps)),
        tempMin: Math.round(Math.min(...temps)),
        condition: mainItem.weather[0].main,
        icon: this.getWeatherIcon(mainItem.weather[0].id, mainItem.weather[0].icon),
        humidity: mainItem.main.humidity
      };
    });
  }

  /**
   * Map OpenWeatherMap weather codes to emoji icons
   * Weather condition codes: https://openweathermap.org/weather-conditions
   */
  private getWeatherIcon(weatherId: number, iconCode: string): string {
    // Check if it's night time (icon code ends with 'n')
    const isNight = iconCode.endsWith('n');

    if (weatherId >= 200 && weatherId < 300) {
      return '‚õàÔ∏è'; // Thunderstorm
    } else if (weatherId >= 300 && weatherId < 400) {
      return 'üå¶Ô∏è'; // Drizzle
    } else if (weatherId >= 500 && weatherId < 600) {
      return 'üåßÔ∏è'; // Rain
    } else if (weatherId >= 600 && weatherId < 700) {
      return '‚ùÑÔ∏è'; // Snow
    } else if (weatherId >= 700 && weatherId < 800) {
      return 'üå´Ô∏è'; // Atmosphere (mist, fog, etc.)
    } else if (weatherId === 800) {
      return isNight ? 'üåô' : '‚òÄÔ∏è'; // Clear
    } else if (weatherId === 801) {
      return isNight ? '‚òÅÔ∏è' : '‚õÖ'; // Few clouds
    } else if (weatherId > 801) {
      return '‚òÅÔ∏è'; // Cloudy
    }

    return '‚õÖ'; // Default
  }

  /**
   * Handle HTTP errors
   */
  private handleError(error: any): Observable<never> {
    let errorMessage = 'An error occurred while fetching weather data';

    if (error.error instanceof ErrorEvent) {
      // Client-side error
      errorMessage = `Error: ${error.error.message}`;
    } else if (error.status) {
      // Server-side error
      switch (error.status) {
        case 401:
          errorMessage = 'Invalid API key';
          break;
        case 404:
          errorMessage = 'City not found';
          break;
        case 429:
          errorMessage = 'API rate limit exceeded';
          break;
        default:
          errorMessage = `Error: ${error.message}`;
      }
    }

    console.error('Weather API Error:', error);
    return throwError(() => new Error(errorMessage));
  }
}

import { CommonModule } from '@angular/common';
import { Component, Input, OnInit, inject } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { HttpClientModule } from '@angular/common/http';
import { WeatherService, WeatherResponse } from './weather.service';

export interface WeatherData {
  location: string;
  temperature: number;
  condition: string;
  humidity: number;
  windSpeed: number;
  icon: string;
}

@Component({
  selector: 'weather-widget',
  standalone: true,
  imports: [CommonModule, FormsModule, HttpClientModule],
  providers: [WeatherService],
  template: `
    <div class="weather-widget" [ngClass]="themeClass">
      <!-- Search Bar -->
      <div class="weather-search" *ngIf="enableSearch">
        <input
          type="text"
          [(ngModel)]="searchCity"
          (keyup.enter)="searchWeather()"
          placeholder="Enter city name..."
          class="search-input"
        />
        <button (click)="searchWeather()" class="search-button">üîç</button>
        <button (click)="getCurrentLocationWeather()" class="location-button" title="Use my location">üìç</button>
      </div>

      <!-- Loading State -->
      <div *ngIf="loading" class="weather-loading">
        <div class="spinner"></div>
        <p>Loading weather data...</p>
      </div>

      <!-- Error State -->
      <div *ngIf="error && !loading" class="weather-error">
        <span class="error-icon">‚ö†Ô∏è</span>
        <p>{{ error }}</p>
        <button (click)="retry()" class="retry-button">Try Again</button>
      </div>

      <!-- Weather Content -->
      <div *ngIf="!loading && !error" class="weather-content">
        <div class="weather-header">
          <h2 class="location">{{ weatherData.location }}</h2>
          <span class="date">{{ currentDate }}</span>
        </div>

        <div class="weather-main">
          <div class="weather-icon">{{ weatherData.icon }}</div>
          <div class="temperature">{{ weatherData.temperature }}¬∞{{ unit }}</div>
        </div>

        <div class="weather-condition">{{ weatherData.condition }}</div>

        <div class="weather-details">
          <div class="detail-item">
            <span class="detail-label">Humidity</span>
            <span class="detail-value">{{ weatherData.humidity }}%</span>
          </div>
          <div class="detail-item">
            <span class="detail-label">Wind Speed</span>
            <span class="detail-value">{{ weatherData.windSpeed }} {{ windUnit }}</span>
          </div>
        </div>

        <!-- Refresh Button -->
        <button *ngIf="enableSearch" (click)="refreshWeather()" class="refresh-button" title="Refresh">
          üîÑ
        </button>
      </div>
    </div>
  `,
  styleUrls: ['./weather-widget.css'],
})
export class WeatherWidgetComponent implements OnInit {
  private weatherService = inject(WeatherService);

  /** Enable search functionality */
  @Input()
  enableSearch: boolean = false;

  /** Initial city to load */
  @Input()
  initialCity: string = 'The Hague';

  /** Use real API data (if false, uses mock data) */
  @Input()
  useRealData: boolean = false;

  /** Weather data to display */
  @Input()
  weatherData: WeatherData = {
    location: 'The Hague',
    temperature: 72,
    condition: 'Partly Cloudy',
    humidity: 65,
    windSpeed: 12,
    icon: '‚õÖ',
  };

  /** Temperature unit (C or F) */
  @Input()
  unit: 'C' | 'F' = 'F';

  /** Wind speed unit */
  @Input()
  windUnit: 'mph' | 'km/h' = 'mph';

  /** Theme of the widget */
  @Input()
  theme: 'light' | 'dark' | 'gradient' = 'light';

  /** Widget size */
  @Input()
  size: 'small' | 'medium' | 'large' = 'medium';

  currentDate: string = '';
  searchCity: string = '';
  loading: boolean = false;
  error: string = '';
  lastSearchedCity: string = '';

  ngOnInit() {
    this.updateCurrentDate();
    this.searchCity = this.initialCity;
    this.lastSearchedCity = this.initialCity;

    // Load weather data if real API is enabled
    if (this.useRealData && this.enableSearch) {
      this.loadWeather(this.initialCity);
    }
  }

  /**
   * Search weather by city name
   */
  searchWeather() {
    if (!this.searchCity.trim()) {
      this.error = 'Please enter a city name';
      return;
    }
    this.loadWeather(this.searchCity.trim());
  }

  /**
   * Get weather for current location using geolocation
   */
  async getCurrentLocationWeather() {
    this.loading = true;
    this.error = '';

    try {
      const coords = await this.weatherService.getCurrentPosition();
      const units = this.unit === 'C' ? 'metric' : 'imperial';

      this.weatherService.getWeatherByCoordinates(coords.latitude, coords.longitude, units)
        .subscribe({
          next: (data) => this.handleWeatherData(data),
          error: (err) => this.handleError(err)
        });
    } catch (err: any) {
      this.loading = false;
      this.error = err.message || 'Unable to get your location';
    }
  }

  /**
   * Refresh current weather data
   */
  refreshWeather() {
    if (this.lastSearchedCity) {
      this.loadWeather(this.lastSearchedCity);
    }
  }

  /**
   * Retry after error
   */
  retry() {
    if (this.lastSearchedCity) {
      this.loadWeather(this.lastSearchedCity);
    } else {
      this.error = '';
    }
  }

  /**
   * Load weather data from API
   */
  private loadWeather(city: string) {
    this.loading = true;
    this.error = '';
    this.lastSearchedCity = city;

    const units = this.unit === 'C' ? 'metric' : 'imperial';

    this.weatherService.getWeatherByCity(city, units).subscribe({
      next: (data) => this.handleWeatherData(data),
      error: (err) => this.handleError(err)
    });
  }

  /**
   * Handle successful weather data response
   */
  private handleWeatherData(data: WeatherResponse) {
    this.weatherData = {
      location: data.location,
      temperature: data.temperature,
      condition: data.condition,
      humidity: data.humidity,
      windSpeed: data.windSpeed,
      icon: data.icon
    };
    this.loading = false;
    this.error = '';
  }

  /**
   * Handle API errors
   */
  private handleError(err: any) {
    this.loading = false;
    this.error = err.message || 'Failed to load weather data';
    console.error('Weather widget error:', err);
  }

  /**
   * Update current date display
   */
  private updateCurrentDate() {
    this.currentDate = new Date().toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  }

  get themeClass(): string {
    return `weather-widget--${this.theme} weather-widget--${this.size}`;
  }
}

